import React from 'react';

function Greeting (){
    let curdate = new Date();
    curdate = curdate.getHours();
    let greeting = {};
    const greetingColor = {};

    if (curdate > 1 && curdate < 12 ) {
        greeting = "Good Morning";
        greetingColor.color = "green";
    }
    else if (curdate >= 12 && curdate < 17)  {
        greeting = "Good AfterNoon";
        greetingColor.color = "orange";
    }
    else if (curdate >= 17 && curdate < 21) {
        greeting = "Good Evening";
        greetingColor.color = "purple";
    }
    else {
        greeting = "Good Night";
        greeting.color = "black";
    }

    return (
        <>
            <div className="styleContainer"> <h1 className="greetingWish">Hello Ayush <span style = {greetingColor}>{greeting}</span> </h1></div>
        </>
    )
} export default Greeting;
