import React from 'react';
function Cards () {
    return (
        <div className = "cards">
            <div className="card">
                <img src="http://wallpapercave.com/wp/wp4056410.jpg" alt="myPic" className="card__img"/>
                <div className="card__info">
                    <span className="card__category">A Netflix Original Series</span>
                    <h3 className="card__title">Dark</h3>
                    <a href="https://www.netflix.com/in/title/80100172" target="_blank">
                        <button>Watch Now</button>
                    </a>
                </div>
            </div>
        </div>
    )
} export default Cards;
