import React from 'react';
import Heading from './Heading';
import Para from './Para';

let curdate = new Date(2021, 5, 6, 18);
curdate = curdate.getHours();
let greeting = {};
const greetingColor = {};

if (curdate > 1 && curdate < 12 ) {
    greeting = "Good Morning";
    greetingColor.color = "green";
}
else if (curdate >= 12 && curdate < 17)  {
    greeting = "Good AfterNoon";
    greetingColor.color = "orange";
}
else if (curdate >= 17 && curdate < 21) {
    greeting = "Good Evening";
    greetingColor.color = "black";
}
else {
    greeting = "Good Night";
}

function App() {
    return (
        <>
            {/* <Para/>
            <Heading />
            <Para /> */}
        <h1>Hello <span style = {greetingColor}>{greeting}</span>, time is {curdate} </h1>

        </>
    )
}

export default App;
